    else

    {

        $FileName = 0x00,0x00,0x69,0x00,0x6e,0x00,0x64,0x00

        $file_name_length = 0x00,0x00

    }

    if($ExtraInfo)

    {

        [Byte[]]$desired_access = 0x80,0x00,0x10,0x00

        [Byte[]]$file_attributes = 0x00,0x00,0x00,0x00

        [Byte[]]$share_access = 0x00,0x00,0x00,0x00

